
public class test2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		int a=10,b=20;
		System.out.println("before swapping");
		System.out.println("a= "+a+"  " +"b= "+b);
		System.out.println("after swapping");
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println("a= "+a+"  " +"b= "+b);
	}

}
